import axios from 'axios'
import store from '@/store'

export function interceptor() {
  axios.interceptors.request.use(
    request => {
      request.headers.common.Authorization = `Bearer ${store.StateToken}`;
      return request
    },
    error => {
      return Promise.reject(error)
    }
  )
}
