import axios from 'axios'

export const interceptor = (token) => {
  axios.interceptors.request.use(
    request => {
      if (request.url.includes('api')) request.headers['Bearer'] = `${token}`
      return request
    },
    error => {
      return Promise.reject(error)
    }
  )
}

export default interceptor;
